/**
 * Multi-AI MCP Server Types
 *
 * v3.0.0 - Infrastructure layer only
 * OpenManager VIBE Project
 * WSL Environment Optimized
 */
/**
 * Supported AI providers.
 * @see {@link https://example.com/supported-providers} for more details.
 */
export type AIProvider = 'codex' | 'gemini' | 'qwen';
/**
 * Structured error for detailed diagnostics.
 */
export interface AIError {
  code: string; // e.g., 'timeout', 'api_error', 'invalid_request'
  message: string;
  details?: unknown; // For additional context or stack trace
}
/**
 * Detailed token usage statistics.
 */
export interface TokenUsage {
  prompt: number;
  completion: number;
  total: number;
}
/**
 * AI response structure.
 */
export interface AIResponse {
  requestId: string; // Unique identifier for tracing
  provider: AIProvider;
  response: string;
  tokens?: TokenUsage;
  responseTime: number; // in milliseconds
  success: boolean;
  error?: AIError;
}
/**
 * Specific status for progress notifications.
 */
export type ProgressStatus = 'queued' | 'processing' | 'streaming' | 'completed' | 'failed';
/**
 * Progress notification callback
 * Used to send progress updates during long-running AI operations.
 */
export type ProgressCallback = (provider: AIProvider, status: ProgressStatus, elapsed: number) => void;
/**
 * Query complexity levels for adaptive timeout.
 */
export type QueryComplexity = 'simple' | 'medium' | 'complex';
/**
 * Timeout configuration for query complexity levels.
 * Enforces that all complexity levels are defined.
 */
export type TimeoutConfig = Record<QueryComplexity, number>;
